export default function getForms() {
    const forms = document.querySelectorAll('form');

    forms.forEach(elem => {
        postForm(elem.id);
    })
    
};

function postData(data) {

    return fetch('./server.php', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(data),
        credentials: 'include'
    });
    
};

function isValid(target) {
    let status = true,
        test;
    target.forEach(key => {
        if (key.type === 'tel') {
            test = /^(\+{1})(\d{6,10})$/.test(key.value);
            if (!test) {
                status = false;
            }
        }
        if (key.type === 'email') {
            test = /^[a-zA-Z.\-_]{1,64}@[a-zA-Z]{4,32}.[a-zA-Z]{2,12}$/.test(key.value);
            if (!test) {
                status = false;
            }
        }
        if (key.type === 'text') {
            test = /^[А-я](\s?)$/.test(key.value);
            if (!test) {
                status = false;
            }
        }
        if(key.className === 'mess') {
            test = /^[А-я]([\s,!.:?\-]?)$/.test(key.value);
            if (!test) {
                status = false;
            } 
        }
    })
    if (!test) alert('Форма заполняется неверно!');
    return status;
};

function postForm(selector) {
    /* Обработка promise */
    const form = document.getElementById(selector);
    const inputs = form.querySelectorAll('input');

    const notification = document.createElement('div'),
        loadNotif = 'Загрузка...',
        successNotif = 'Заявка успешно доставлена!',

        // const successNotif = () => {
        //     notification.textContent = 'Заявка успешно доставлена!';
        //     form.reset();
        //     preloader.classList.remove('preloader-active');
        // }

        failedNotif = 'Ошибка! Нет ответа от сервера!';
    notification.style.color = 'red';

    const resetNotif = () => {
        let timeout = setTimeout(() => {
            notification.textContent = '';
        }, 3000);
    }

    form.addEventListener('submit', (e) => {
        e.preventDefault();

        const status = isValid(inputs);
        if (!status) return alert('Форма заполнена неверно!');

        const preloader = document.querySelector('.preloader-inner');
        preloader.classList.add('preloader-active');

        form.appendChild(notification);
        notification.textContent = loadNotif;

        const formData = new FormData(form),
            data = {};

        formData.forEach((key, index) => {
            data[index] = key
        });

        // postData(data, () => {
        //     notification.textContent = successNotif;
        //     form.reset();
        //     preloader.classList.remove('preloader-active');
        // }, (error) => {
        //     notification.textContent = failedNotif;
        //     console.error(error);
        // });

        postData(data)
            .then((response) => {
                if (response.status !== 200) {
                    throw new Error('network status not 200')
                }
                notification.textContent = successNotif;
                resetNotif();
            })
            .catch(error => {
                notification.textContent = failedNotif;
                console.error(error);
                resetNotif();
            })
            .finally(() => {
                form.reset();
                preloader.classList.remove('preloader-active');
            })

    })
};
